.preloader {
  @extend %cover;
  @extend %center;

  background: $color-pampas;
  color: $color-cod-grey;
  height: 100%;
  overflow: hidden;
  position: fixed;
  transform-origin: 0 0;
  z-index: z("preloader");
  width: 100vw;

  &--loaded {
    transform: scaleY(0);
  }
}

.preloader__number {
  @extend %title-200;

  max-width: 117.2rem;
  overflow: hidden;
  text-align: center;
  white-space: nowrap;
}

.preloader__number__label,
.preloader__number__year {
  display: inline-block;
  opacity: 0;
}

.preloader__box {
  align-items: center;
  bottom: 6.4rem;
  display: flex;
  justify-content: space-around;
  left: 50%;
  position: absolute;
  transform: translateX(-50%);
  width: 100%;

  @include media("<tablet") {
    bottom: 3rem;
  }
}

.stars {
  animation: spin 4s linear infinite;
