sources.styles\\components\\_navigation.scss:
.navigation {
  @extend %cover;

  pointer-events: none;
  position: fixed;
  z-index: z("navigation");
}

.navigation__link {
  pointer-events: auto;
  position: absolute;
  top: 4rem;
  transition: all $ease-out-expo 0.4s;

  &:first-child {
    cursor: pointer;
    left: 4rem;

    @include media("<phone") {
      left: 2rem;
    }
  }

  &:last-child {
    right: 4rem;

    @include media("<phone") {
      right: 2rem;
    }
  }

  &:hover {
    opacity: 0.75;
  }
}

sources.styles\\utils\\_functions.scss:
@function z($name) {
  @if index($z-indexes, $name) {
    @return (length($z-indexes) - index($z-indexes, $name)) + 1;
  } @else {
    @warn 'There is no item "#{$name}" in this list; Choose one of: #{$z-indexes}';

    @return null;
  }
}

sources.styles\\components\\_footer.scss:
.footer {
}

.footer__label__wrapper {
  align-items: center;
  display: flex;
  justify-content: center;
}

.footer__stars {
  animation: spin-and-scale 6s ease infinite;
  fill: $color-pampas;
  height: 1.4rem;
  width: 1.4rem;

  @include media("<=tablet") {
    height: 1.25rem;
    width: 1.25rem;
  }

  @include media("<phone") {
    height: 1.05rem;
    width: 1.05rem;
  }
}

.footer__label {
  @extend %description-18;
  font-family: $font-supreme;
  font-weight: 100;

  margin: 30.2rem 6rem;

  @include media("<=tablet") {
    margin: 26rem 0;
  }

  @include media("<phone") {
    margin: 20rem 0;
  }
}

.footer__box {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
}

.footer__link {
  @extend %description-150;

  font-weight: bold;
  line-height: 1.2;
  transition: all $ease-out-expo 0.4s;
  width: fit-content;

  &:nth-child(even) {
    justify-self: end;
  }

  &:hover {
    opacity: 0.75;
  }
}

.footer__name {
  cursor: pointer;
  fill: $color-pampas;
  margin-top: 3rem;
  margin-bottom: 1.5rem;
  transition: all $ease-out-expo 0.8s;

  &:hover {
    opacity: 0.75;
  }
}

