sources.styles\\pages\\home\\_works.scss:
.home__works__label {
  margin: 18.1rem auto 30.2rem;
  max-width: 51.1rem;
  line-height: 1.6;

  @include media("<tablet") {
    margin: 17rem 0 26rem;
  }

  @include media("<phone") {
    margin: 10rem 0 20rem;
    margin-left: 2rem;
    width: 35rem;
  }
}

.home__marquee__title {
  @extend %title-200;

  display: inline-block;
  margin-bottom: 26.6rem;
  white-space: nowrap;

  span {
    display: inline-block;
    vertical-align: middle;
  }

  @include media("<tablet") {
    margin-bottom: 16rem;
  }

  @include media("<phone") {
    margin-bottom: 10.8rem;
  }
}

.home__works__list {
  align-items: start;
  display: flex;
  flex-wrap: wrap;
  grid-template-rows: auto;
  margin-left: 4rem;
  margin-right: 4rem;

  @include media("<tablet") {
    grid-row-gap: 15rem;
  }

  @include media("<phone") {
    margin-left: 2rem;
    margin-right: 2rem;
    grid-row-gap: 6rem;
  }
}

.home__works__list__link {
  &:nth-child(odd):not(:first-of-type) {
    margin-top: -15%;

    @include media("<large") {
      margin-top: 15%;
    }

    @include media("<tablet") {
      margin-top: 0;
    }
  }

  &:nth-child(even) {
    margin-left: auto;
    margin-top: 15%;

    @include media("<tablet") {
      margin-top: 0;
      margin-left: 0;
    }
  }

  &:nth-child(5) {
    // margin-top: 0% !important;
  }

  @include media("<phone") {
    line-height: 1.2;
  }
}

.home__works__list__header {
  align-items: center;
  display: flex;
  justify-content: space-between;

  .home__works__list__link:nth-child(1) & {
    width: 96rem;

    @include media("<tablet") {
      width: calc(100vw - 8rem);
    }

    @include media("<phone") {
      width: 100%;
    }
  }

  .home__works__list__link:nth-child(2) & {
    width: 66.3rem;

    @include media("<tablet") {
      width: calc(100vw - 8rem);
    }

    @include media("<phone") {
      width: 100%;
    }
  }

  .home__works__list__link:nth-child(4) & {
    width: 66.3rem;

    @include media("<tablet") {
      width: calc(100vw - 8rem);
    }

    @include media("<phone") {
      width: 100%;
    }
  }

  .home__works__list__link:nth-child(3) & {
    width: 73.3rem;

    @include media("<tablet") {
      width: calc(100vw - 8rem);
    }

    @include media("<phone") {
      width: 100%;
    }
  }

  .home__works__list__link:nth-child(5) & {
    width: 95.4rem;

    @include media("<tablet") {
      width: calc(100vw - 8rem);
    }

    @include media("<phone") {
      width: 100%;
    }
  }

  .home__works__list__link:nth-child(6) & {
    width: 73.3rem;

    @include media("<tablet") {
      width: calc(100vw - 8rem);
    }

    @include media("<phone") {
      width: 100%;
    }
  }
}

